// fix sub nav on scroll
function processScroll(){var a,b=$win.scrollTop();if(b>=refinementsBarTop&&!isFixed){isFixed=1;$refinementsBar.addClass("refinements-bar-fixed")}else if(b<=refinementsBarTop&&isFixed){isFixed=0;$refinementsBar.removeClass("refinements-bar-fixed")}}var $win=$(window),$refinementsBar=$(".refinements-bar"),refinementsBarTop=$(".refinements-bar").length&&$(".refinements-bar").offset().top-0,isFixed=0;processScroll();$win.on("scroll",processScroll);