$(".menubar li").click(function(){if(!$(this).hasClass("active")){$("#subnav").is(":hidden")&&$("#subnav").slideDown(100,function(){$(".nav-marketing").fadeIn(1e3)});$(this).addClass("active")}else if($(this).hasClass("active")){$("#subnav").slideUp(100,function(){$(".nav-marketing").hide()});$(this).removeClass("active")}$(this).siblings().stop(!0,!0).removeClass("active");var a=$(this).attr("class");arr=a.split(" ");navToOpen=arr.shift();$("#"+navToOpen).siblings().hide();document.getElementById(navToOpen).style.display="block"});var mouseIsIn=!1;$("nav").hover(function(){mouseIsIn=!0},function(){mouseIsIn=!1});$("body").mouseup(function(){if(!mouseIsIn){$("#subnav").slideUp(100,function(){$(".nav-marketing").hide()});$(".menubar li").removeClass("active")}});